function MainBody() {
  // State to hold the selected course code
  const [selectedCourse, setSelectedCourse] = useState("");

  // Mapping of course codes to students
  const courseData = {
    "Course 1": ["Ann Andersson", "Bertil Bertilsson", "Calle Carlsson"],
    "Course 2": ["Disa Danielsson", "Erika Eriksson", "Fredrik Fredriksson"],
    "Course 3": ["Greta Gustafsson", "Helena Holm", "Ivan Ivarsson"],
  };

  // Handle dropdown selection
  const handleCourseChange = (event) => {
    setSelectedCourse(event.target.value);
  };

  return (
    <div className="main-body">
      {/* Dropdown Menus */}
      <div className="dropdowns">
        <div className="dropdown">
          <label>Kurskod</label>
          <select onChange={handleCourseChange}>
            <option value="">Select Course</option>
            {Object.keys(courseData).map((course) => (
              <option key={course} value={course}>
                {course}
              </option>
            ))}
          </select>
        </div>

        {/* Other Dropdowns (Dummy for demonstration) */}
        <div className="dropdown">
          <label>Uppgift i Canvas</label>
          <select>
            <option value="">Select Task</option>
          </select>
        </div>

        <div className="dropdown">
          <label>Modul i Ladok</label>
          <select>
            <option value="">Select Module</option>
          </select>
        </div>
      </div>

      {/* Conditional Rendering of Students */}
      <div className="students-list">
        {selectedCourse && (
          <>
            <h3>Students in {selectedCourse}</h3>
            <ul>
              {courseData[selectedCourse].map((student, index) => (
                <li key={index}>{student}</li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
}

export default MainBody;
