// MainBody.js
import React, { useState } from "react";
import './MainBody.css';

function MainBody() {
  // State to hold the selected course code
  const [selectedCourse, setSelectedCourse] = useState("");

  // Updated mapping of course codes to students with grades
  const courseData = {
    "Course 1": [
      { name: "John Pork", grade: "G" },
      { name: "Freak Bob", grade: "VG" },
      { name: "Skibidi Prime", grade: "G" }
    ],
    "Course 2": [
      { name: "KSI", grade: "VG" },
      { name: "Drake", grade: "G" },
      { name: "Fredrik Fredriksson", grade: "VG" }
    ],
    "Course 3": [
      { name: "Greta Gustafsson", grade: "G" },
      { name: "Helena Holm", grade: "VG" },
      { name: "Ivan Ivarsson", grade: "G" }
    ]
  };

  // Handle dropdown selection
  const handleCourseChange = (event) => {
    setSelectedCourse(event.target.value);
  };

  return (
    <div className="main-body">
      {/* Dropdown Menus */}
      <div className="dropdowns">
        <div className="dropdown">
          <label>Kurskod</label>
          <select onChange={handleCourseChange}>
            <option value="">Select Course</option>
            {Object.keys(courseData).map((course) => (
              <option key={course} value={course}>
                {course}
              </option>
            ))}
          </select>
        </div>

        {/* Other Dropdowns (Dummy for demonstration) */}
        <div className="dropdown">
          <label>Uppgift i Canvas</label>
          <select>
            <option value="">Select Task</option>
          </select>
        </div>

        <div className="dropdown">
          <label>Modul i Ladok</label>
          <select>
            <option value="">Select Module</option>
          </select>
        </div>
      </div>

      {/* Conditional Rendering of Students with Grades */}
      <div className="students-list">
        {selectedCourse && (
          <>
            <div className="student-list-header">
              <span>Namn</span>
              <span>Omdöme i Canvas</span>
            </div>
            <ul>
              {courseData[selectedCourse].map((student, index) => (
                <li key={index} className="student-item">
                  <span className="student-name">{student.name}</span>
                  <span className="student-grade">{student.grade}</span>
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
}

export default MainBody;
